// Search.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Search = () => {
    const [query, setQuery] = useState('');
    const history = useNavigate();

    const exampleContent = [
        'product',
        'solution',
        'partner',
        'news',
        'support',
        'training'
    ];

    const handleSearch = () => {
        // Check if query matches any content
        const contentExists = exampleContent.includes(query.toLowerCase());
        if (contentExists) {
            // Logic to display search results or perform search action
            console.log(`Content found for query: ${query}`);
        } else {
            // If content doesn't exist, redirect to search page
            history.push(`/search?q=${query}`);
        }
    };

    return (
        <div>
            <button onClick={handleSearch}>Open Search</button>
            <input
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Enter your query..."
            />
            <button onClick={handleSearch}>Search</button>
        </div>
    );
};

export default Search;
