import React from 'react';

const HeroSection = () => {
    return (
        <div className="container-fluid">
            <div className="row align-items-center justify-content-center vh-100">
                <div className="col-md-6 ">
                    <h1 className="display-4">Learning Services</h1>
                    <p className="lead">Sage Learn­ing Ser­vices will as­sist you in gain­ing ex­pert­ise, using a vari­ety of solu­tions that auto­mate manual and time con­sum­ing busi­ness pro­cesses. Our ex­pert train­ers deep dive into our soft­ware, provid­ing you with the know how to use the product to its full po­ten­tial. From Ac­count­ing, to Payroll HR, to In­tel­li­gence Re­port­ing, and more, we offer all you need</p>
                    <button className="btn btn-primary btn-lg">Learn More</button>
                </div>
                <div className="col-md-6 text-center">
                    <img className='ounded' src='https://www.sage.com/en-za/-/media/images/sagedotcom/southafrica/resources/learning-services/learning-services-hero-1920x640/desktop.jpg?extension=webp'/>
                </div>
            </div>
        </div>
    );
};

export default HeroSection;
